// Function to display usage information
#define EXPIRY_YEAR 2024
#define EXPIRY_MONTH 10
#define EXPIRY_DAY 13

void usage() {
    printf("Usage: ./VAMPIRE ip port time threads\n");
    exit(1);
}

void check_expiry() {
    time_t now;
    struct tm expiry_time;

    time(&now);
    struct tm *current_time = localtime(&now);

    expiry_time.tm_year = EXPIRY_YEAR - 1900;  // tm_year is the number of years since 1900
    expiry_time.tm_mon = EXPIRY_MONTH - 1;     // tm_mon is 0-11
    expiry_time.tm_mday = EXPIRY_DAY;
    expiry_time.tm_hour = 0;
    expiry_time.tm_min = 0;
    expiry_time.tm_sec = 0;
    expiry_time.tm_isdst = -1;

    time_t expiry = mktime(&expiry_time);

    if (difftime(now, expiry) > 0) {
        printf("This file has expired. Contact the owner @DEMON_ROCKY\n");
        exit(1);
    }
}
